package btclightclient

import (
	"errors"
	"fmt"

	"testing"

	"github.com/btcsuite/btcd/chaincfg"
	"github.com/btcsuite/btcd/wire"
	"gotest.tools/assert"
)

type commonTestCaseData struct {
	headers []string
	header  string
}

type testCaseMap map[string]commonTestCaseData

var HEADERS = []string{
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
	"",
}

func CommonTestCases() testCaseMap {
	tcs := map[string]commonTestCaseData{
		"Append a fork": {
			headers: HEADERS,
			header:  "",
		},
		"Create fork": {
			headers: HEADERS,
			header:  "",
		},
		"Insert failed because fork too old": {
			headers: HEADERS,
			header:  "",
		},
		"Block doesn't belong to any fork!": {
			headers: HEADERS,
			header:  "",
		},
	}

	return tcs
}

func TestInsertHeader(t *testing.T) {

	commonTestCase := CommonTestCases()

	testCases := map[string]error{
		"Append a fork":                      nil,
		"Create fork":                        nil,
		"Insert failed because fork too old": errors.New("fork too old"),
		"Block doesn't belong to any fork!":  errors.New("block doesn't belong to any fork"),
	}

	run := func(t *testing.T, testcase string) {
		data := commonTestCase[testcase]

		decodedHeader := make([]wire.BlockHeader, len(data.headers))
		btcHeader, _ := BlockHeaderFromHex(data.header)
		for id, str := range data.headers {
			h, _ := BlockHeaderFromHex(str)
			decodedHeader[id] = h
		}
		lc := NewBTCLightClientWithData(&chaincfg.RegressionNetParams, decodedHeader, 0)
		lcErr := lc.InsertHeader(btcHeader)

		expectedErr := testCases[testcase]
		if expectedErr == nil {
			assert.NilError(t, lcErr)
		} else {
			assert.Error(t, lcErr, expectedErr.Error())
		}

	}

	for testcase := range testCases {
		t.Run(testcase, func(t *testing.T) {
			run(t, testcase)
		})
	}
}

func TestCleanup(t *testing.T) {
	var err error

	tcs := CommonTestCases()
	decodedHeader := make([]wire.BlockHeader, len(HEADERS))
	for id, str := range HEADERS {
		h, _ := BlockHeaderFromHex(str)
		decodedHeader[id] = h
	}
	lc := NewBTCLightClientWithData(&chaincfg.RegressionNetParams, decodedHeader, 0)

	// test-case1
	// b1 <-b2 <- b3  .... b9
	// run cleanup
	// b2 <- b3  .... b9

	data := tcs["Append a fork"]
	btcHeader, _ := BlockHeaderFromHex(data.header)

	beforeCheckpoint := lc.btcStore.LatestCheckPoint()

	lcErr := lc.InsertHeader(btcHeader)

	// we ignore this common test
	if lcErr != nil {
		t.Fatal("Should not return error when insert header")
	}

	err = lc.CleanUpFork()
	assert.NilError(t, err)

	afterCheckpoint := lc.btcStore.LatestCheckPoint()

	assert.Assert(t, beforeCheckpoint.Height+1 == afterCheckpoint.Height)

	// notthing change
	// test-case2
	// b3  ... b8 <- b9 <- b10
	//                  \- c10
	// run cleanup
	// b3  ... b8 <- b9 <- b10
	//                  \- c10
	err = lc.CleanUpFork()
	assert.NilError(t, err)

	notupdateCheckpoint := lc.btcStore.LatestCheckPoint()

	assert.Assert(t, notupdateCheckpoint == afterCheckpoint)

	data = tcs["Create fork"]
	btcHeader, _ = BlockHeaderFromHex(data.header)

	err = lc.InsertHeader(btcHeader)
	assert.NilError(t, err)

	// test-case3
	// b1 <- b2 <- b3  .... b9
	//          \- c3
	//    \- d2 <- d3 <- d4
	// run cleanup
	// b2 <- b3  .... b9
	//    \- c3

	headers := []string{
		"",
		"",
		"",
		"",
		"",
		"",
		"",
	}

	for id, h := range headers {
		btcHeader, _ := BlockHeaderFromHex(h)
		lcErr := lc.InsertHeader(btcHeader)

		// we ignore this common test
		if lcErr != nil {
			fmt.Println(lcErr, id)
			t.Fatal("Should not return error when insert header")
		}
		err := lc.CleanUpFork()
		assert.NilError(t, err)
		fmt.Println(btcHeader.BlockHash())
	}

	listFork := lc.btcStore.LatestBlockHashOfFork()

	assert.Assert(t, len(listFork) == 1)
}
